<?phprequire_once( dirname( dirname( dirname( dirname( dirname( __FILE__ ) ) ) ) ) . '/wp-load.php' );require_once( dirname( dirname( dirname( dirname( dirname( __FILE__ ) ) ) ) ) . '/wp-includes/option.php' );require_once( dirname( dirname( __FILE__ ) ) . '/class/class-sm-helper.php' );if( isset( $_SERVER[ 'REQUEST_METHOD' ] ) && $_SERVER[ 'REQUEST_METHOD' ] !== 'POST' ){    header( 'Content-Type: text/plain' );    die( 'This file only accepts POST requests only.' );}global $HTTP_RAW_POST_DATA;if( empty( $HTTP_RAW_POST_DATA ) ){    $data = file_get_contents( 'php://input' );}else{    $data = &$HTTP_RAW_POST_DATA;}$sm_connector = new Smart_SM_Connector();$sm_connector->ProcessRequest( $data );exit;?>